import React from 'react';
import Filters from '../components/Filters';
import Card from '../components/Card';

const Home = () => {
    const handleFilterChange = (filters) => {
        console.log('Filters changed:', filters);
        // Add filter logic here later
    };

    return (
        <div className="min-h-screen bg-gray-50">
            <div className="container mx-auto px-4 py-8">
                <div className="text-center mb-8">
                    <h1 className="text-4xl font-bold text-gray-900 mb-4">Find Great Deals in Your Neighborhood</h1>
                    <p className="text-xl text-gray-600">Buy and sell items locally with ease</p>
                </div>

                <div className="bg-white rounded-lg shadow-md p-6 mb-8">
                    <Filters onFilterChange={handleFilterChange} />
                </div>

                <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-6">
                    {/* Sample listings for demo */}
                    <div className="bg-white rounded-lg shadow-md overflow-hidden">
                        <div className="h-48 bg-gray-200 flex items-center justify-center">
                            <span className="text-gray-500">No Image</span>
                        </div>
                        <div className="p-4">
                            <h3 className="text-lg font-semibold mb-2">Sample Listing</h3>
                            <p className="text-gray-600 mb-2">Create your first listing to see it here!</p>
                            <div className="flex justify-between items-center">
                                <span className="text-xl font-bold text-blue-600">$0</span>
                                <button className="bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700">
                                    View Details
                                </button>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default Home;
